import React from "react";
import "./About.css";
import AboutImg from "../../assets/about.jpg";
import CV from "../../assets/HarshVijay.pdf";
import Info from "./Info";

const About = () => {
  return (
    <section className="about section" id="about">
      <h2 className="section__title">About Me</h2>
      <span className="section__subtitle">My introduction</span>

      <div className="about__container container grid">
        <img src={AboutImg} alt="" className="about__img" />

        <div className="about__data">
          <Info />

          <p className="about__description">
            Hello there I'm Harsh Vijay, and I'm now pursuing a Master of
            Computer Applications (MCA) degree. I am an enthusiastic and
            dedicated learner with a strong interest in becoming a software
            developer and web developer. I have developed a strong foundation in
            web development technologies, database management systems, and
            programming languages including Java, C/C++, and JavaScript
            throughout my academic career. I am always seeking for ways to
            increase my knowledge and stay current with the most recent trends
            and advancements in the pitch. I'm excited to get some real-world
            experience in the software and web development fields as a newbie.
            My strong analytical thinking and problem-solving abilities will
            allow me to approach complicated challenges uniquely and
            effectively.
          </p>

          <a download="" href={CV} className="button button--flex">Download Resume</a>
        </div>
      </div>
    </section>
  );
};

export default About;
